(718, 172, '_wp_attached_file', '2019/03/tien-ich-nam-sao-dang-cap-1.jpg'),
(719, 172, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:1140;s:6:"height";i:662;s:4:"file";s:39:"2019/03/tien-ich-nam-sao-dang-cap-1.jpg";s:5:"sizes";a:5:{s:9:"thumbnail";a:4:{s:4:"file";s:39:"tien-ich-nam-sao-dang-cap-1-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:39:"tien-ich-nam-sao-dang-cap-1-300x174.jpg";s:5:"width";i:300;s:6:"height";i:174;s:9:"mime-type";s:10:"image/jpeg";}s:12:"medium_large";a:4:{s:4:"file";s:39:"tien-ich-nam-sao-dang-cap-1-768x446.jpg";s:5:"width";i:768;s:6:"height";i:446;s:9:"mime-type";s:10:"image/jpeg";}s:5:"large";a:4:{s:4:"file";s:40:"tien-ich-nam-sao-dang-cap-1-1024x595.jpg";s:5:"width";i:1024;s:6:"height";i:595;s:9:"mime-type";s:10:"image/jpeg";}s:20:"featuredImageProject";a:4:{s:4:"file";s:39:"tien-ich-nam-sao-dang-cap-1-720x520.jpg";s:5:"width";i:720;s:6:"height";i:520;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:12:{s:8:"aperture";s:1:"0";s:6:"credit";s:0:"";s:6:"camera";s:0:"";s:7:"caption";s:0:"";s:17:"created_timestamp";s:1:"0";s:9:"copyright";s:0:"";s:12:"focal_length";s:1:"0";s:3:"iso";s:1:"0";s:13:"shutter_speed";s:1:"0";s:5:"title";s:0:"";s:11:"orientation";s:1:"0";s:8:"keywords";a:0:{}}}'),
(720, 66, 'project_utilities', 'Được thiết kế tỉ mỉ đến từng chi tiết nhỏ, tối ưu tầm nhìn toàn cảnh về sông Sài Gòn, trung tâm thành phố và mang ánh sáng thiên nhiên vào nhà, đem đến cho khách hàng những trải nghiệm sống hoàn hảo, yên tĩnh chỉ có tại ROSENA. Với nhiều tiện ích và thiết kế sang trọng sẽ đem lại cho Quý khách hàng một cuộc sống như khách sạn, được hưởng toàn bộ dịch vụ và tiện nghi tối ưu, hiện đại nhất tại đây. Nên nhấn mạnh với quy mô dự án không lớn (98 căn), dự án đang tích hợp khá đầy đủ tiện ích nội khu, điều mà chỉ thấy ở các dự án quy mô lớn.\r\n\r\nLấy cảm hứng thiết kế hiện đại mang phong cách sang trọng, dự án có đầy đủ các tiện ích cao cấp nội khu hoàn chỉnh cho cư dân trải nghiệm cuộc sống tiện nghi: Hồ bơi, Phòng tập Gym, Café Terrace, Concierge, Office văn phòng, Mailbox, Hầm để xe rộng rãi, an toàn và hiện đại.\r\n\r\n&nbsp;\r\n\r\n<img class="alignnone wp-image-172 size-full" src="http://localhost/hoangphuc/wp-content/uploads/2019/03/tien-ich-nam-sao-dang-cap-1.jpg" alt="" width="1140" height="662" />'),
