(1443, 269, '_wp_attached_file', '2019/04/ve-bao-long-land.jpg'),
(1444, 269, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:554;s:6:"height";i:525;s:4:"file";s:28:"2019/04/ve-bao-long-land.jpg";s:5:"sizes";a:3:{s:9:"thumbnail";a:4:{s:4:"file";s:28:"ve-bao-long-land-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:28:"ve-bao-long-land-400x379.jpg";s:5:"width";i:400;s:6:"height";i:379;s:9:"mime-type";s:10:"image/jpeg";}s:20:"featuredImageProject";a:4:{s:4:"file";s:28:"ve-bao-long-land-554x520.jpg";s:5:"width";i:554;s:6:"height";i:520;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:12:{s:8:"aperture";s:1:"0";s:6:"credit";s:0:"";s:6:"camera";s:0:"";s:7:"caption";s:0:"";s:17:"created_timestamp";s:1:"0";s:9:"copyright";s:0:"";s:12:"focal_length";s:1:"0";s:3:"iso";s:1:"0";s:13:"shutter_speed";s:1:"0";s:5:"title";s:0:"";s:11:"orientation";s:1:"1";s:8:"keywords";a:0:{}}}'),
