(2210, 315, '_intro_field_block_4_img', 'field_5ca47ea0a7881'),
(2211, 315, 'intro_value_title', 'Giá trị cốt lõi'),
(2212, 315, '_intro_value_title', 'field_5ca4814d3f798'),
(2213, 315, 'intro_value_text', 'Các sản phẩm bất động sản của Bảo Long Land tập trung vào việc đáp ứng được nhu cầu thực tế của thị trường, của khách hàng. Một sản phẩm bất động sản do Bảo Long Land đầu tư và phát triển sẽ đem đến cho khách hàng ba giá trị cốt lõi: Giá trị sử dụng, giá trị khai thác và giá trị bền vững giữ được nét đẹp, bền vững theo thời gian.'),
(2214, 315, '_intro_value_text', 'field_5ca4818a3f799'),
(2215, 315, 'intro_value_block_1_title', 'Giá trị sử dụng'),
(2216, 315, '_intro_value_block_1_title', 'field_5ca483025b068'),
(2217, 315, 'intro_value_block_1_desc', 'Ngay tại thời điểm hiện tại và tương lai đều phù hợp với nhu cầu sử dụng. Qua nhiều thế hệ giá trị sử dụng vẫn không thay đổi.'),
(2218, 315, '_intro_value_block_1_desc', 'field_5ca483355b069'),
