(2221, 315, 'intro_value_block_2_title', 'Giá trị khai thác'),
(2222, 315, '_intro_value_block_2_title', 'field_5ca483a05b06b'),
(2223, 315, 'intro_value_block_2_desc', 'Khách hàng có thể không sử dụng trực tiếp nhưng có thể khai thác gián tiếp bằng việc cho thuê với giá trị tài sản theo thời gian không ngừng gia tăng.'),
